select fldFirstName, fldLastName, fldAdvisorFirstName, fldAdvisorLastName, fldMajor, fldMinor, fldYear, fldTerm, fldCourseName, fldDepartment, fldCourseNumber, tblCourses.fldCredits from tblPlan join tblSemesterPlan on tblSemesterPlan.fnkPlanId = tblPlan.pmkPlanId join tblSemesterPlanCourses on tblSemesterPlan.fnkPlanId = tblSemesterPlanCourses.fnkPlanId and tblSemesterPlan.fldTerm = tblSemesterPlanCourses.fnkTerm and tblSemesterPlan.fldYear = tblSemesterPlanCourses.fnkYear join tblStudents on tblPlan.fnkStudentId = tblStudents.pmkStudentId join tblCourses on tblSemesterPlanCourses.fnkCourseId = tblCourses.pmkCourseId join tblAdvisors on tblPlan.fnkAdvisorId = tblAdvisors.pmkAdvisorId where fldLastName = "Girdzis" order by tblSemesterPlanCourses.fldDisplayOrder;