select distinct fnkSectionId, fldFirstName, fldLastName from ((tblEnrolls inner join tblStudents on tblEnrolls.fnkStudentId = tblStudents.pmkStudentId) inner join tblSections on tblEnrolls.fnkCourseId = tblSections.fnkCourseId) inner join tblCourses on tblSections.fnkCourseId = tblCourses.pmkCourseId where fldCourseNumber = 148 and fldDepartment = "CS" order by fnkSectionId, fldLastName, fldFirstName; 