select tblTeachers.fldFirstName, tblTeachers.fldLastName, count(tblStudents.fldFirstName),fldSalary, (fldSalary/count(tblStudents.fldFirstName)) as IBB from (tblTeachers join tblSections on tblTeachers.pmkNetId = tblSections.fnkTeacherNetId join tblEnrolls on tblSections.fldCRN = tblEnrolls.fnkSectionId join tblStudents on tblEnrolls.fnkStudentId = tblStudents.pmkStudentId) where fldType != "LAB" group by fldFirstName;